import React from 'react';
import UserService from '../services/UserService';

class UserComponent extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            users:[]
        }
    }

    componentDidMount(){
        UserService.getUsers().then((response) => {
            this.setState({ users: response.data})
        });
    }

    render (){
        return (
            <div>
                <h1 className = "text-center"> Users List</h1>

                <button className="btn btn-refresh" onClick={this.componentDidMount}>
                    Refresh
                </button>

                <table className = "table table-striped">
                    <thead>
                        <tr>
                            <td> User Id <input type="text" name="userId" /> </td>
                            <td> User First Name <input type="text" name="fname" /> </td>
                            <td> User Last Name <input type="text" name="lname" /></td>
                            <td> User Email Id <input type="text" name="email" /></td>
                        </tr>

                    </thead>
                    <tbody>
                        {
                            this.state.users.map(
                                user =>
                                <tr key = {user.id}>
                                     <td> {user.id}</td>
                                     <td> {user.identifier}</td>
                                     <td> {user.lastName}</td>
                                     <td> {user.email}</td>
                                </tr>
                            )
                        }

                    </tbody>
                </table>

            </div>

        )
    }
}

export default UserComponent
